% clc
% N = 200;
% i = 1;
% j = 1;
% cosCoefficients = zeros(29,200);
% sinCoefficients = zeros(29,200);
% for k = 4:32
% for n = 0:N-1
%     cosCoefficients(j,i) = cos((2*pi*k*n)/N);
%     sinCoefficients(j,i) = -sin((2*pi*k*n)/N);
%     i=i+1;
% end
% i = 1;
% j = j + 1;
% end
% j = 1;
% 
% writematrix(cosCoefficients)
% type 'cosCoefficients.txt'
% 
% writematrix(sinCoefficients)
% type 'sinCoefficients.txt'

clc
cosCoefficients = zeros(1,200);
sinCoefficients = zeros(1,200);
N=200;
k=30;
i=1;
for n = 0:N-1
cosCoefficients(1,i) = cos((2*pi*k*n)/N);
sinCoefficients(1,i) = -sin((2*pi*k*n)/N);
i=i+1;
end

writematrix(cosCoefficients)
type 'cosCoefficients.txt'
 
writematrix(sinCoefficients)
type 'sinCoefficients.txt'

